import React, { useState } from "react";
import { Link } from "react-router-dom";
import { ChevronDown, ChevronUp } from "lucide-react";

export default function SidebarMenu({ onClose }) {
  const [openMenus, setOpenMenus] = useState({});

  const toggleMenu = (menu) => {
    setOpenMenus((prev) => ({ ...prev, [menu]: !prev[menu] }));
  };

  const menuItems = [
    { title: "Orders", subItems: ["Purchase Orders", "Returns", "Invoices"] },
    { title: "Items", subItems: ["Catalog", "Upload"] },
    { title: "Advertising", subItems: ["Campaigns", "Reports"] },
    { title: "Merchandising", subItems: ["Deals", "Promotions"] },
    { title: "Growth", subItems: ["Opportunities", "Insights"] },
    { title: "Reports", subItems: ["Sales", "Inventory"] },
    {
      title: "Payments",
      subItems: [
        <Link
          to="/invoice-management"
          key="invoices"
          className="text-blue-600 hover:underline"
        >
          Invoices
        </Link>,
        <a
          key="remittance"
          href="https://vendorcentral.amazon.com/hz/vendor/members/remittance/home?ref_=vc_xx_subNav"
          target="_blank"
          rel="noopener noreferrer"
          className="text-blue-600 hover:underline"
        >
          Remittances
        </a>,
      ],
    },
    { title: "Integration", subItems: [] },
    { title: "Learn", subItems: [] },
  ];

  return (
    <div className="fixed top-0 left-0 w-72 h-full bg-white z-50 shadow-lg overflow-y-auto">
      {/* Header */}
      <div className="flex items-center justify-between p-4 border-b">
        <span className="font-bold text-lg">Menu</span>
        <button onClick={onClose} className="text-2xl font-semibold">
          Ã—
        </button>
      </div>

      {/* Menu */}
      <div className="p-4 space-y-3 font-medium text-gray-900">
        {menuItems.map(({ title, subItems }) => (
          <div key={title}>
            <div
              className={`flex justify-between items-center cursor-pointer ${
                subItems.length ? "hover:text-teal-700" : ""
              }`}
              onClick={() => subItems.length && toggleMenu(title)}
            >
              <span>{title}</span>
              {subItems.length > 0 &&
                (openMenus[title] ? (
                  <ChevronUp className="w-4 h-4" />
                ) : (
                  <ChevronDown className="w-4 h-4" />
                ))}
            </div>
            {openMenus[title] && subItems.length > 0 && (
              <div className="ml-4 mt-2 space-y-1 text-sm">
                {subItems.map((item, idx) =>
                  typeof item === "string" ? (
                    <div key={idx} className="text-gray-700">
                      {item}
                    </div>
                  ) : (
                    <div key={idx}>{item}</div>
                  )
                )}
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}
