// src/components/MenuBar.jsx
import { useState } from 'react';
import { FaChevronDown, FaChevronUp } from 'react-icons/fa';

const MenuBar = () => {
  const [openSections, setOpenSections] = useState({});

  const toggleSection = (section) => {
    setOpenSections((prev) => ({ ...prev, [section]: !prev[section] }));
  };

  const menuItems = [
    { label: 'Orders', subItems: ['Purchase Orders', 'Invoices', 'Shipments'] },
    { label: 'Items', subItems: ['Catalog', 'Item Setup'] },
    { label: 'Merchandising', subItems: ['A+ Content', 'Images'] },
    { label: 'Growth', subItems: ['Programs', 'Opportunities'] },
    { label: 'Reports', subItems: ['Operational', 'Analytics'] },
    { label: 'Learn', subItems: ['Help', 'Guides'] },
  ];

  return (
    <div className="w-64 bg-white border-r h-full p-4 shadow">
      <h2 className="text-lg font-semibold mb-4">Menu</h2>
      <ul className="space-y-2">
        {menuItems.map((item) => (
          <li key={item.label}>
            <button
              onClick={() => toggleSection(item.label)}
              className="flex justify-between items-center w-full text-left text-sm font-medium text-gray-800 hover:text-blue-600"
            >
              {item.label}
              {openSections[item.label] ? <FaChevronUp /> : <FaChevronDown />}
            </button>
            {openSections[item.label] && (
              <ul className="pl-4 mt-1 space-y-1 text-sm text-gray-600">
                {item.subItems.map((sub) => (
                  <li key={sub} className="hover:underline cursor-pointer">{sub}</li>
                ))}
              </ul>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default MenuBar;
