import React, { useState, useRef, useEffect } from "react";
import { AiOutlineDown, AiOutlineUp, AiOutlineInfoCircle } from "react-icons/ai";

const BusinessSnapshot = () => {
  const [showDropdown, setShowDropdown] = useState(false);
  const dropdownRef = useRef(null);

  // Click outside closes dropdown
  useEffect(() => {
    function handleClickOutside(event) {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setShowDropdown(false);
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  return (
    <div className="bg-white rounded-xl shadow p-6 flex flex-col md:flex-row gap-4 items-center justify-between relative">
      {/* Shipped Revenue - with Dropdown */}
      <div className="flex-1">
        <button
          className="flex items-center gap-2 font-semibold text-gray-800 text-base hover:bg-gray-100 px-2 py-1 rounded"
          onClick={() => setShowDropdown((v) => !v)}
        >
          Shipped Revenue{" "}
          {showDropdown ? (
            <AiOutlineUp size={16} />
          ) : (
            <AiOutlineDown size={16} />
          )}
        </button>
        <div className="text-2xl font-bold mt-2">$97.86</div>
        {/* Dropdown Panel */}
        {showDropdown && (
          <div
            ref={dropdownRef}
            className="absolute z-20 left-4 top-20 bg-white rounded-lg shadow-lg border border-gray-200 w-80 p-4"
          >
            <div className="flex items-center mb-2">
              <span className="font-semibold text-gray-900 mr-2">Shipped Revenue</span>
              <AiOutlineInfoCircle className="text-gray-400" />
            </div>
            <div className="text-gray-700 text-sm mb-2">
              July 1, 2025 â€“ July 9, 2025
            </div>
            <button className="mt-2 w-full py-2 rounded bg-gray-100 hover:bg-blue-100 font-medium text-gray-800">
              Go to Shipped Revenue
            </button>
          </div>
        )}
      </div>
      {/* Other columns as usual */}
      <div className="flex-1">
        <div className="text-gray-600 text-xs">Open PO Qty</div>
        <div className="text-2xl font-bold mt-2">0</div>
      </div>
      <div className="flex-1">
        <div className="text-gray-600 text-xs">On Hand Units</div>
        <div className="text-2xl font-bold mt-2">767</div>
      </div>
    </div>
  );
};

export default BusinessSnapshot;
