// src/components/TabBar.jsx
import React from "react";
import { useNavigate, useLocation } from "react-router-dom";
import { FiHome } from "react-icons/fi";

const tabs = [
  { label: "Home", path: "/retail-analytics", icon: <FiHome /> },
  { label: "Sales", path: "/shipped-revenue" },
  { label: "Inventory", path: "/inventory" },
  { label: "Catalog", path: "/catalog" },
];

export default function TabBar() {
  const navigate = useNavigate();
  const location = useLocation();

  return (
    <div className="flex items-center gap-8 border-b border-gray-200 pt-8 pb-0 px-2 bg-white">
      {tabs.map((tab) => (
        <button
          key={tab.label}
          onClick={() => navigate(tab.path)}
          className={`flex items-center gap-1 font-semibold text-base pb-2 border-b-2 transition-all duration-200 ${
            location.pathname === tab.path
              ? "text-teal-700 border-teal-700"
              : "text-gray-700 border-transparent hover:text-teal-700"
          }`}
        >
          {tab.icon && <span className="text-xl">{tab.icon}</span>}
          {tab.label}
        </button>
      ))}
    </div>
  );
}
