import React, { useState } from "react";
import { Link } from "react-router-dom";
import { ChevronRight } from "lucide-react";

export default function SidebarMenu({ onClose }) {
  const [hovered, setHovered] = useState(null);

  const menuItems = [
    {
      title: "Orders",
      subItems: ["Purchase Orders", "Returns", "Invoices"],
    },
    {
      title: "Items",
      subItems: ["Catalog", "Upload"],
    },
    {
      title: "Advertising",
      subItems: ["Campaigns", "Reports"],
    },
    {
      title: "Merchandising",
      subItems: ["Deals", "Promotions"],
    },
    {
      title: "Growth",
      subItems: ["Opportunities", "Insights"],
    },
    {
      title: "Reports",
      subItems: ["Sales", "Inventory"],
    },
    {
      title: "Payments",
      subItems: [
        <Link
          to="/invoice-management"
          key="inv"
          className="text-blue-600 hover:underline block px-3 py-1"
        >
          Invoices
        </Link>,
        <a
          href="https://vendorcentral.amazon.com/hz/vendor/members/remittance/home?ref_=vc_xx_subNav"
          key="rem"
          target="_blank"
          rel="noopener noreferrer"
          className="text-blue-600 hover:underline block px-3 py-1"
        >
          Remittances
        </a>,
      ],
    },
    { title: "Integration", subItems: [] },
    { title: "Learn", subItems: [] },
  ];

  return (
    <div className="fixed top-0 left-0 w-72 h-full bg-white z-50 shadow-lg overflow-y-auto">
      {/* Header */}
      <div className="flex items-center justify-between p-4 border-b">
        <span className="font-bold text-lg">Menu</span>
        <button onClick={onClose} className="text-2xl font-semibold">
          Ã—
        </button>
      </div>

      {/* Menu */}
      <div className="relative p-4 space-y-2 font-medium text-gray-900">
        {menuItems.map(({ title, subItems }) => (
          <div
            key={title}
            className="relative group"
            onMouseEnter={() => setHovered(title)}
            onMouseLeave={() => setHovered(null)}
          >
            <div className="flex justify-between items-center cursor-pointer hover:text-teal-700 pr-3">
              <span>{title}</span>
              {subItems.length > 0 && <ChevronRight className="w-4 h-4" />}
            </div>

            {hovered === title && subItems.length > 0 && (
              <div className="absolute left-72 top-0 w-52 bg-white border shadow rounded-md z-50 p-2 space-y-1">
                {subItems.map((item, idx) =>
                  typeof item === "string" ? (
                    <div key={idx} className="text-sm hover:underline px-2 py-1 cursor-pointer">
                      {item}
                    </div>
                  ) : (
                    <div key={idx}>{item}</div>
                  )
                )}
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}
