import React, { useState } from 'react';
import { FaChevronRight } from 'react-icons/fa';

const menuItems = [
  {
    label: 'Orders',
    subItems: [
      'Purchase Orders',
      'Direct Fulfillment Orders',
      'Purchase Order Forecasts',
      'Supply Chain Programs',
    ],
  },
  { label: 'Items', subItems: ['Catalog'] },
  { label: 'Merchandising', subItems: ['A+ Content'] },
  { label: 'Growth', subItems: ['Programs'] },
  { label: 'Reports', subItems: ['Analytics'] },
  { label: 'Learn', subItems: ['Help'] },
];

export default function MenuBar() {
  const [hovered, setHovered] = useState(null);

  return (
    <div className="p-4 w-56 bg-white h-full">
      <h2 className="text-lg font-semibold mb-4">Menu</h2>
      <ul className="space-y-2">
        {menuItems.map((item) => (
          <li
            key={item.label}
            className="relative"
            onMouseEnter={() => setHovered(item.label)}
            onMouseLeave={() => setHovered(null)}
          >
            <button
              className="flex justify-between items-center w-full text-left text-base font-medium text-gray-800 hover:text-blue-600 py-2 px-2 rounded"
            >
              {item.label}
              {item.subItems && <FaChevronRight />}
            </button>

            {/* Flyout Submenu */}
            {hovered === item.label && item.subItems && (
              <ul
                className="absolute top-0 left-full ml-1 bg-white border shadow-xl rounded min-w-[240px] z-30"
              >
                {item.subItems.map((sub) => (
                  <li
                    key={sub}
                    className="px-6 py-3 text-base text-gray-800 hover:bg-gray-100 cursor-pointer whitespace-nowrap"
                  >
                    {sub}
                  </li>
                ))}
              </ul>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}
