import React, { useState } from "react";
import { FaChevronRight } from "react-icons/fa";
import { IoMdClose } from "react-icons/io";

const menuItems = [
  {
    label: "Orders",
    subItems: [
      "Purchase Orders",
      "Direct Fulfillment Orders",
      "Purchase Order Forecasts",
      "Supply Chain Programs",
    ],
  },
  {
    label: "Items",
    subItems: [
      "Catalog",
      "Improve Listing Quality",
      "Add Products",
      "Bulk product submissions",
      "Upload Images",
      "Upload and Manage Videos",
      "Amazon Packaging Certification",
      "Manage case packs",
      "Product Certificates",
      "Manage Product Compliance",
      "Manage catalog feeds",
    ],
  },
  {
    label: "Merchandising",
    subItems: ["Manage Your Experiments", "Business Discounts"],
  },
  {
    label: "Growth",
    subItems: [
      "Add Prime to your site",
      "Sustainability Solutions Hub",
      "Multi-Channel Fulfillment (MCF)",
      "Direct Fulfillment",
      "Growth Opportunities",
    ],
  },
  {
    label: "Reports",
    subItems: [
      "Retail Analytics",
      "Brand Analytics",
      "Voice of the Customer",
      "Product Prep and Packaging",
      "Concession Hub",
    ],
  },
  {
    label: "Learn",
    subItems: ["Learning Hub", "Webinars"],
  },
];

export default function Sidebar({ onClose }) {
  const [hovered, setHovered] = useState(null);

  return (
    <div className="fixed top-0 left-0 h-full w-64 bg-white border-r border-gray-200 z-50 flex flex-col shadow-lg">
      <div className="flex items-center justify-between px-6 py-5 border-b border-gray-100">
        <span className="font-bold text-lg text-gray-800">Menu</span>
        <button
          onClick={onClose}
          className="text-2xl text-gray-500 hover:bg-gray-200 rounded-full p-1"
        >
          <IoMdClose />
        </button>
      </div>
      <ul className="flex-1 px-0 py-2 relative">
        {menuItems.map((item) => (
          <li
            key={item.label}
            className="group flex items-center px-6 py-3 cursor-pointer hover:bg-gray-100 text-base font-medium text-gray-900 relative"
            onMouseEnter={() => setHovered(item.label)}
            onMouseLeave={() => setHovered(null)}
          >
            <span>{item.label}</span>
            {item.subItems && (
              <FaChevronRight className="ml-auto text-xs text-gray-400 group-hover:text-gray-700" />
            )}
            {hovered === item.label && item.subItems && (
              <ul className="absolute top-0 left-full ml-1 bg-white border shadow-xl rounded min-w-[260px] z-50">
                {item.subItems.map((sub) => (
                  <li
                    key={sub}
                    className="px-6 py-3 text-base text-gray-800 hover:bg-gray-100 cursor-pointer whitespace-nowrap"
                  >
                    {sub}
                  </li>
                ))}
              </ul>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}
