import React, { useState, useRef, useEffect } from "react";
import { AiOutlineDown, AiOutlineUp, AiOutlineInfoCircle } from "react-icons/ai";
import { BsThreeDotsVertical } from "react-icons/bs";

const ShippedRevenueDropdown = () => {
  const [open, setOpen] = useState(false);
  const dropdownRef = useRef(null);

  useEffect(() => {
    function handleClickOutside(event) {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setOpen(false);
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  return (
    <div className="relative inline-block" ref={dropdownRef}>
      {/* The label - can wrap your existing "Shipped Revenue" header */}
      <button
        onClick={() => setOpen((v) => !v)}
        className="flex items-center gap-1 font-semibold text-gray-900"
      >
        Shipped Revenue{" "}
        {open ? <AiOutlineUp size={16} /> : <AiOutlineDown size={16} />}
      </button>
      {/* Dropdown Panel */}
      {open && (
        <div className="absolute left-0 top-8 w-80 z-20 bg-white border border-gray-200 rounded-lg shadow-lg p-4">
          <div className="flex items-center justify-between mb-1">
            <div className="flex items-center gap-2 font-semibold text-gray-900">
              Shipped Revenue <AiOutlineInfoCircle className="text-gray-400" />
            </div>
            <BsThreeDotsVertical className="text-gray-500" />
          </div>
          <div className="text-gray-700 text-sm mb-2">
            July 1, 2025 â€“ July 9, 2025
          </div>
          <button className="w-full py-2 mt-2 rounded bg-gray-100 hover:bg-blue-100 font-medium text-gray-800">
            Go to Shipped Revenue
          </button>
        </div>
      )}
    </div>
  );
};

export default ShippedRevenueDropdown;
