// MenuBar.jsx
import React, { useState } from 'react';
import { FaChevronDown, FaChevronUp } from 'react-icons/fa';

const MenuBar = () => {
  const [open, setOpen] = useState({});

  const toggle = (label) => {
    setOpen((prev) => ({ ...prev, [label]: !prev[label] }));
  };

  const menuItems = [
    { label: 'Orders', subItems: ['Purchase Orders'] },
    { label: 'Items', subItems: ['Catalog'] },
    { label: 'Merchandising', subItems: ['A+ Content'] },
    { label: 'Growth', subItems: ['Programs'] },
    { label: 'Reports', subItems: ['Analytics'] },
    { label: 'Learn', subItems: ['Help'] },
  ];

  return (
    <div className="p-4">
      {/* REMOVE THE "Menu" HEADING HERE */}
      <ul className="space-y-2">
        {menuItems.map((item) => (
          <li key={item.label}>
            <button
              onClick={() => toggle(item.label)}
              className="flex justify-between items-center w-full text-left text-sm font-medium text-gray-800 hover:text-blue-600"
            >
              {item.label}
              {open[item.label] ? <FaChevronUp /> : <FaChevronDown />}
            </button>
            {open[item.label] && (
              <ul className="pl-4 mt-1 space-y-1 text-sm text-gray-600">
                {item.subItems.map((sub) => (
                  <li key={sub} className="hover:underline cursor-pointer">{sub}</li>
                ))}
              </ul>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default MenuBar;
