// src/components/TabBar.jsx
import React from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { AiOutlineHome } from "react-icons/ai";

const TABS = [
  { name: "Home", path: "/retail-analytics", icon: <AiOutlineHome className="text-xl mr-1" /> },
  { name: "Sales", path: "/shipped-revenue" },
  { name: "Inventory", path: "/inventory" },
  { name: "Catalog", path: "/catalog" },
];

export default function TabBar() {
  const location = useLocation();
  const navigate = useNavigate();

  return (
    <div className="flex items-center gap-8 border-b border-gray-200 pb-1 mb-6 bg-white">
      {TABS.map((tab) => {
        const isActive = location.pathname === tab.path ||
          // Home also active for "/" and "/retail-analytics"
          (tab.path === "/retail-analytics" && (location.pathname === "/" || location.pathname === "/retail-analytics"));
        return (
          <div
            key={tab.name}
            className={
              `flex items-center font-semibold text-base cursor-pointer pb-1 select-none ` +
              (isActive
                ? "text-teal-700 border-b-2 border-teal-700"
                : "text-gray-700 hover:text-teal-700")
            }
            onClick={() => navigate(tab.path)}
          >
            {tab.icon && tab.icon}
            <span>{tab.name}</span>
          </div>
        );
      })}
    </div>
  );
}
