import React from 'react';


const ChevronDownIcon = () => (
  <svg
    xmlns="http://www.w3.org/2000/svg"
    className="h-4 w-4 ml-1 text-gray-600"
    fill="none"
    viewBox="0 0 24 24"
    stroke="currentColor"
    strokeWidth={2}
  >
    <path strokeLinecap="round" strokeLinejoin="round" d="M19 9l-7 7-7-7" />
  </svg>
);

const BusinessSnapshot = () => {
  return (
    <div className="bg-white border border-gray-300 shadow-sm rounded-lg p-4">
      <h2 className="text-sm font-semibold text-gray-800 mb-3">Business Snapshot</h2>
      <div className="grid grid-cols-1 sm:grid-cols-3 divide-x divide-gray-300">
        {/* Shipped Revenue */}
        <div className="px-4">
          <div className="flex items-center text-sm text-gray-700 mb-1">
            <span>Shipped Revenue</span>
            <ChevronDownIcon />
          </div>
          <div className="text-xl font-semibold text-gray-900">$97.86</div>
        </div>

        {/* Open PO Qty */}
        <div className="px-4">
          <div className="flex items-center text-sm text-gray-700 mb-1">
            <span>Open PO Qty</span>
            <ChevronDownIcon />
          </div>
          <div className="text-xl font-semibold text-gray-900">0</div>
        </div>

        {/* On Hand Units */}
        <div className="px-4">
          <div className="flex items-center text-sm text-gray-700 mb-1">
            <span>On Hand Units</span>
            <ChevronDownIcon />
          </div>
          <div className="text-xl font-semibold text-gray-900">767</div>
        </div>
      </div>
    </div>
  );
};

export default BusinessSnapshot;
