import { FiMenu } from "react-icons/fi";
import { Link } from "react-router-dom";
import SidebarMenu from "./SidebarMenu";

export default function TopNav() {
  const [menuOpen, setMenuOpen] = useState(false);

  return (
    <>
      <div className="flex items-center justify-between bg-gray-900 text-white px-4 py-3">
        <div className="flex items-center gap-4">
          <FiMenu
            className="text-2xl cursor-pointer"
            onClick={() => setMenuOpen(true)}
          />
          <Link to="/" className="flex flex-col leading-tight hover:opacity-80 transition">
            <div className="flex items-center gap-1">
              <span className="text-white font-bold text-xl">amazon</span>
              <span className="text-white text-lg">vendor central</span>
            </div>
            <span className="text-orange-400 text-sm tracking-wide ml-1">â†’ from a to z</span>
          </Link>
        </div>
        <div className="text-sm">US - Sportsman Supply Inc.</div>
      </div>

      {menuOpen && <SidebarMenu onClose={() => setMenuOpen(false)} />}
    </>
  );
}
