import React, { useState } from "react";
import { Link } from "react-router-dom";
import { ChevronRight } from "lucide-react";

export default function SidebarMenu({ onClose }) {
  const [openMenu, setOpenMenu] = useState(null);

  const toggleMenu = (title) => {
    setOpenMenu(openMenu === title ? null : title);
  };

  const menuItems = [
    {
      title: "Orders",
      subItems: [
        "Purchase Orders",
        "Direct Fulfillment Orders",
        "Purchase Order Forecasts",
        "Supply Chain Programs",
      ],
    },
    {
      title: "Items",
      subItems: [
        "Catalog",
        "Improve Listing Quality",
        "Add Products",
        "Bulk product submissions",
        "Upload Images",
        "Upload and Manage Videos",
        "Amazon Packaging Certification",
        "Manage case packs",
        "Product Certificates",
        "Manage Product Compliance",
        "Manage catalog feeds",
      ],
    },
    {
      title: "Merchandising",
      subItems: ["Manage Your Experiments", "Business Discounts"],
    },
    {
      title: "Growth",
      subItems: [
        "Add Prime to your site",
        "Sustainability Solutions Hub",
        "Multi-Channel Fulfillment (MCF)",
        "Direct Fulfillment",
        "Growth Opportunities",
      ],
    },
    {
      title: "Reports",
      subItems: [
        "Retail Analytics",
        "Brand Analytics",
        "Voice of the Customer",
        "Product Prep and Packaging",
        "Concession Hub",
      ],
    },
    {
      title: "Learn",
      subItems: ["Learning Hub", "Webinars"],
    },
    {
      title: "Payments",
      subItems: [],
    },
  ];

  return (
    <div className="fixed top-0 left-0 w-72 h-full bg-white z-[999] shadow-lg overflow-y-auto">
      {/* Header */}
      <div className="flex items-center justify-between p-4 border-b">
        <span className="font-bold text-lg">Menu</span>
        <button onClick={onClose} className="text-2xl font-semibold">Ã—</button>
      </div>

      {/* Menu List */}
      <div className="relative p-4 font-medium text-gray-900 space-y-2">
        {menuItems.map(({ title, subItems }) => (
          <div key={title} className="relative">
            <button
              className="w-full flex items-center justify-between px-2 py-2 rounded hover:text-teal-700"
              onClick={() => toggleMenu(title)}
            >
              <span>{title}</span>
              <ChevronRight className="w-4 h-4" />
            </button>

            {/* Dropright submenu */}
            {openMenu === title && subItems.length > 0 && (
              <div className="absolute top-0 left-full ml-2 bg-white border shadow rounded w-72 z-50 p-2">
                {subItems.map((item, i) => (
                  <div key={i} className="px-3 py-2 text-sm hover:bg-gray-100 rounded cursor-pointer">
                    {item}
                  </div>
                ))}
              </div>
            )}

            {/* Special case: Payments with links */}
            {openMenu === "Payments" && title === "Payments" && (
              <div className="absolute top-0 left-full ml-2 bg-white border shadow rounded w-72 z-50 p-2">
                <Link
                  to="/invoice-management"
                  className="block px-3 py-2 text-sm text-blue-600 hover:bg-gray-100 rounded"
                >
                  Invoices
                </Link>
                <a
                  href="https://vendorcentral.amazon.com/hz/vendor/members/remittance/home?ref_=vc_xx_subNav"
                  target="_blank"
                  rel="noopener noreferrer"
                  className="block px-3 py-2 text-sm text-blue-600 hover:bg-gray-100 rounded"
                >
                  Remittances
                </a>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}
